select count(x.RGD_ID), r.SPECIES_TYPE_KEY 
from RGD_ACC_XDB x, RGD_IDS r
where x.RGD_ID=r.RGD_ID
and r.OBJECT_STATUS='ACTIVE'
and x.ACC_ID=x.LINK_TEXT
and x.XDB_KEY=23
group by r.SPECIES_TYPE_KEY


