x.XDB_KEY=2


--know this query is right.. 
select acc.* from 
DEV_1.RGD_ACC_XDB acc, DEV_1.REFERENCES ref
where acc.RGD_ID=ref.RGD_ID
and acc.XDB_KEY=2 and 
acc.ACC_ID in
(
select x.ACC_ID
from DEV_1.RGD_ACC_XDB x, DEV_1.REFERENCES r
where 
x.RGD_ID=r.RGD_ID
and x.XDB_KEY=2
group by x.ACC_ID
having count(x.ACC_ID) > 1
)
order by acc.ACC_ID



select acc.*, 
rg.*
from 
RGD_ACC_XDB acc, REFERENCES ref, RGD_IDS rg
where acc.RGD_ID=ref.RGD_ID
and acc.RGD_ID=rg.RGD_ID
and rg.OBJECT_STATUS='ACTIVE'
and acc.XDB_KEY=2 and
acc.ACC_ID in
(
select x.ACC_ID
from RGD_ACC_XDB x, REFERENCES r, RGD_IDS rgd
where 
x.RGD_ID=r.RGD_ID
and x.RGD_ID=rgd.RGD_ID
and rgd.OBJECT_STATUS='ACTIVE'
and x.XDB_KEY=2
group by x.ACC_ID
having count(x.ACC_ID) > 1
)
order by acc.ACC_ID


